import React from "react";
import "./App.css";


function App() {

    const emptyTodo = {
        title: "",
        description: ""
    };

    const [todos, setTodos] = React.useState([]);
    const [newTodo, setNewTodo] = React.useState(emptyTodo);

    const getAllTodos = () => fetch("http://localhost:3000/todos")
    .then(function(response) {
        return response.json();
    })
    .then(function(myJson) {
        setTodos(myJson)
    });

    function fakeIdGenerator() {
        return Math.round(Math.random() * 1000000000);
    }
    const addEntry = (todo) => {
        setTodos(
            todos.concat({
                ...todo,
                id: fakeIdGenerator()
            })
            );
            fetch("http://localhost:3000/todos", {
                method: 'POST', // *GET, POST, PUT, DELETE, etc.
                mode: 'cors', // no-cors, cors, *same-origin
                cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
                credentials: 'same-origin', // include, *same-origin, omit
                headers: {
                    'Content-Type': 'application/json',
                    // 'Content-Type': 'application/x-www-form-urlencoded',
                },
                redirect: 'follow', // manual, *follow, error
                referrer: 'no-referrer', // no-referrer, *client
                body: JSON.stringify(todos), // body data type must match "Content-Type" header
            })
            .then(response => response.json());
            setNewTodo(emptyTodo);
        };

        const createOnNewTodoChange = (field) => {
            return (e) => {
                setNewTodo({
                    ...newTodo,
                    [field]: e.target.value
                });
            };
        };

        const deleteEntry = (todoIndex) => {
            const remainingTodos = todos.filter(({ id }) => id !== todoIndex);
            setTodos(remainingTodos);
        };

    return (
        <div className="App">
            <header className="App-header" />
            <ul>
                {todos && todos.map((todo) => (
                    <li key={todo.id}>
                        {todo.title} / {todo.description}
                        <button onClick={() => deleteEntry(todo.id)}>
                            del
                        </button>
                    </li>
                ))}
            </ul>
            <div>
                <input
                    value={newTodo.title}
                    onChange={createOnNewTodoChange("title")}
                />
                <input
                    value={newTodo.description}
                    onChange={createOnNewTodoChange("description")}
                />
                <button onClick={() => addEntry(newTodo)}>add</button>
                <button onClick={() => getAllTodos()}>Get todos</button>
            </div>
        </div>
    );
}

export default App;
