import React from 'react';
import './App.css';

function App() {
  function fakeIdGenerator() {
    return Math.round(Math.random() * 1000000000);
  }
  const allTodos = [{
    id: fakeIdGenerator(),
    title: "vai",
    description:"xablau",
  },
  {
    id: fakeIdGenerator(),
    title: "toamr",
    description:"xablau",
  },
  {
    id: fakeIdGenerator(),
    title: "no",
    description:"xablau",
  }, 
  {
    id: fakeIdGenerator(),
    title: "meio",
    description:"xablau",
  }]

  const emptyTodo = {
    title: "",
    description: ""
  }

  const [todos, setTodos] = React.useState(allTodos);
  const [newTodo, setNewTodo] = React.useState(emptyTodo);

  const addEntry = (todo) => {
    setTodos(todos.concat({
      ...todo,
      id: fakeIdGenerator()
    }));

    setNewTodo(emptyTodo);
  }

  const createOnNewTodoChange = (field) => {
    return (e) => {
      setNewTodo({
        ...newTodo,
        [field]: e.target.value
      });
    }
  }

  const deleteEntry = ( todoIndex ) => {
    const remainingTodos = todos.filter(({id}) => id !== todoIndex)
    setTodos(remainingTodos)
  }

  return (
    <div className="App">
    <header className="App-header" />
        <ul> 
        {todos.map((todo) => 
          <li key={todo.id}>
            {todo.title} / {todo.description} 
            <button onClick={() => deleteEntry(todo.id)}>del</button>
          </li>
        )}
        </ul>
        <div>
          <input value={newTodo.title} onChange={createOnNewTodoChange('title')} />
          <input value={newTodo.description} onChange={(e) => onNewTodoChange('description', e.target.value)} />
          <button onClick={() => addEntry(newTodo)} >add</button>
        </div>
    </div>
  );
}

export default App;
