import React from 'react';
import './App.css';

function App() {

  const allTodos = [{
    id: 0,
    title: "vai",
    description:"xablau",
},
{
  id: 1,
  title: "toamr",
  description:"xablau",
},
{
  id: 2,
  title: "no",
  description:"xablau",
}, 
{
  id: 3,
  title: "meio",
  description:"xablau",
}, 
]

  const [todos, setTodos] = React.useState(allTodos);
  const [newTodo, addNewTodo] = React.useState({});

  const deleteEntry = ( todoIndex ) => {
    const remainingTodos = todos.filter(({id}) => id !== todoIndex)
    setTodos(remainingTodos)
  }

  return (
    <div className="App">
      <header className="App-header" />
      <ul> 
      {todos.map((todo) => 
        <li>{todo}<button onClick={() => deleteEntry(todo.id)}>del</button></li>)}
        </ul>
        <div>
        <input value={newTodo} />
        {/* <button onClick={() => createEntry(input) }> add </button> */}
        </div>
    </div>
  );
}

export default App;
