import React from 'react';
import './App.css';

function App() {

  const allTodos = ["vai", "porra", "desgraça", "filho", "da", "puta"]

  const [todos, setAllTodos] = React.useState(allTodos);

  const DeleteEntry = (todo, todos ) => {
    const index = todos.indexOf(todo)
    todos.splice(index, 1)
    setAllTodos(todos)
    console.log(todos)
  }

  return (
    <div className="App">
      <header className="App-header" />
      <ul> 
      {todos.map(todo => 
        <li>{todo}<button onClick={() => DeleteEntry(todo, todos)}>Del</button></li>)}
        </ul>
        <input />
    </div>
  );
}

export default App;
