import React from "react";
import "./App.css";


function App() {

    const [todos, setTodos] = React.useState([]);
    const [newTodo, setNewTodo] = React.useState(emptyTodo);

    fetch("http://localhost:3000/todos")
    .then(function(response) {
        return response.json();
    })
    .then(function(myJson) {
        console.log(JSON.stringify(myJson));
        setTodos(JSON.stringify(myJson))
        console.log("lista xablautica: ",todos)
    });

    function fakeIdGenerator() {
        return Math.round(Math.random() * 1000000000);
    }

    const emptyTodo = {
        title: "",
        description: ""
    };
    const addEntry = (todo) => {
        setTodos(
            todos.concat({
                ...todo,
                id: fakeIdGenerator()
            })
            );

            setNewTodo(emptyTodo);
        };

        const createOnNewTodoChange = (field) => {
            return (e) => {
                setNewTodo({
                    ...newTodo,
                    [field]: e.target.value
                });
            };
        };
        console.log(todos)

        const deleteEntry = (todoIndex) => {
            const remainingTodos = todos.filter(({ id }) => id !== todoIndex);
            setTodos(remainingTodos);
        };

    return (
        <div className="App">
            <header className="App-header" />
            <ul>
                {todos && todos.map((todo) => (
                    <li key={todo.id}>
                        {todo.title} / {todo.description}
                        <button onClick={() => deleteEntry(todo.id)}>
                            del
                        </button>
                    </li>
                ))}
            </ul>
            <div>
                <input
                    value={newTodo.title}
                    onChange={createOnNewTodoChange("title")}
                />
                <input
                    value={newTodo.description}
                    onChange={createOnNewTodoChange("description")}
                />
                <button onClick={() => addEntry(newTodo)}>add</button>
            </div>
        </div>
    );
}

export default App;
